import React, { Component } from 'react';
import './MyFetch.css'

export default class MyFetch extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            contacts: []
        }
    }

    // componentDidMount() {
    //     this.fetchdata();
    // }

    fetchdata() {
        fetch("https://ghibliapi.herokuapp.com/people", {
	        method: 'get'
        })
	    .then(response => {
            return response.json()
                .then(data => {
                    create(data);
                }
            )
        })
        .catch(err => {
            console.error(err);
        })
        .finally( () => {
            document.getElementById("my-img").style.visibility = "hidden";
        });
    }

    
    
    create = (data) => {
        let table = [];
        let propertyNames = Object.getOwnPropertyNames(data[0]);
        let propertyNamesLength = Object.keys(propertyNames).length;

        // Outer loop to create parent
        for (let i in propertyNames) {
          let children = [];
          //Inner loop to create children
          for (let j in data) {
            children.push(<td>{`Column ${j + 1}`}</td>);
          }
          //Create the parent and add the children
          table.push(<th>{children}</th>);
        }
        return table;
      }
    
    

    render() {
        const {isLoading} = this.state;
        return (
            <div className={`content ${isLoading ? 'is-loading' : ''}`}>
                <img id="my-img" alt="...loading data" src={"https://loading.io/spinners/wave/lg.wave-ball-preloader.gif"}/>
                <table>
                    {this.fetchdata()}
                </table>
            </div>
        );
    }
};
