import React, { Component } from 'react';
import './MyFetch.css'

export default class MyFetch extends Component {
    constructor(props) {
        super(props);
        this.state = {
            isLoading: true,
            users: []
        }
    }

    componentDidMount() {

            fetch("https://ghibliapi.herokuapp.com/people", {
                method: 'get'
            })
            .then(response => {
                return response.json()
                    .then(data => {
                        this.setState({
                            isLoading:false,
                            users: data.users
                        })
                    }
                )
            })
            .catch(error => this.setState({ error, isLoading: false }));
            // .finally( () => {
            //     document.getElementById("my-img").style.visibility = "hidden";
            // });
    }

    

    
    // create(data) {
    //     let table = []
    //     let propertyNames = Object.getOwnPropertyNames(data[0])
        
    //     for (let i in propertyNames) {
    //         table.push(<th>{propertyNames[i]}</th>)
    //     }

    //     for (let i in data) {
    //         let children=[]
    //         for(let j in data[i]) {
    //             children.push(<tr>{data[i][j]}</tr>)
    //         }
    //         table.push(<td>{children}</td>)
    //     }
    //     console.log(table);
    // }
    
    render() {
        const { users, isLoading, error } = this.state;

        if (error) {
        return <p>{error.message}</p>;
        }

        if (isLoading) {
        return <img id="my-img" alt="...loading data" src={"https://loading.io/spinners/wave/lg.wave-ball-preloader.gif"}/>;
        }

        return (
            <ul>
                {users.map(user =>
                <li key={user.objectID}>
                    <a href={user.url}>{user.title}</a>
                </li>
                )}
            </ul>
        );
    }
}
